mapper.select:
select number,name,role_id,sex,age,phone,address,create_time,create_by,
       update_time,update_by,status from user


        and number like concat('%',#{number},'%')


        and name like concat('%',#{name},'%')

    and sex = #{sex}
    and age = #{age}

        and address like concat('%',#{address},'%')

    and status = #{status}
